class TestTermsOfUse(unittest.TestCase):
    @classmethod
    def setUpClass(cls) -> None:
        cls.url = MAIN_URL_HOME
        cls.driver = webdriver.WebDriver(CHROME_DRIVER_LOCATION)
        cls.driver.get(cls.url)
        cls.driver.maximize_window()
        time.sleep(DELAY_SHORT)


    def test__when_terms_of_use_is_showing_data__should_pass(self):
        driver = self.driver
        terms_of_use_btn = driver.find_element_by_link_text('Terms of Use')
        terms_of_use_btn.click()
        time.sleep(DELAY_SHORT)
        terms_of_use_side_bar = driver.find_element_by_class_name('terms-sidebar')
        side_bar_list = terms_of_use_side_bar.find_elements_by_tag_name('li')
        side_bar_link_list = []
        side_bar_name_list = []
        for side_bar in side_bar_list:
            side_bar_link = side_bar.find_element_by_tag_name('a').get_attribute('href')
            side_bar_name = side_bar.text
            if side_bar_link != '':
                side_bar_link_list.append(side_bar_link)
            if side_bar_name != '':
                side_bar_name_list.append(side_bar_name)

        terms_of_use_content = driver.find_element_by_id('myTabContent')
        content_titles = terms_of_use_content.find_elements_by_tag_name('h5')
        content_titles_list = []
        for row in content_titles:
            title = row.text
            if title != '':
                content_titles_list.append(title)

        content_paragraphs = terms_of_use_content.find_elements_by_tag_name('p')
        content_para_list = []
        for row in content_paragraphs:
            para = row.text
            if para != '':
                content_para_list.append(para)

        time.sleep(1)
        if side_bar_name_list != SIDE_BAR_NAME_LIST_DATA and side_bar_link_list != SIDE_BAR_LINK_LIST_DATA and content_titles_list != CONTENT_TITLE_LIST_DATA and content_para_list != CONTENT_PARA_LIST_DATA:
            self.fail()
            
    @classmethod
    def tearDownClass(cls) -> None:
        cls.driver.close()
        cls.driver.quit()


if __name__ == '__main__':
    unittest.main()
